	public static String gen(Map<String, Integer> word_df, Map<String, Set<WordandTF>> link_words, Map<String, Url_data> urldata,  int n){
		
		String out = "";
		
		/// calcutate idf
		Map<String, Float> word_idf =  new HashMap<String,Float>();
		Iterator<String> it = word_df.keySet().iterator();
		while(it.hasNext()){
			String word = it.next();
			word_idf.put(word, (float) Math.log10((n/(float)word_df.get(word))));	
		}
		
		System.out.println("word idf:"+word_idf);
		

		System.out.println("link_words:"+link_words);
		
		
		it = link_words.keySet().iterator();
		
		out += "<searchresults>";
		
		while(it.hasNext()){
			String url = it.next();
			
			out += "<result>";
			out += "<url>"+url+"</url>";
			
			/// for each word
			float sum = 0;
			Iterator<WordandTF> it3 = link_words.get(url).iterator();
			while(it3.hasNext()){
				WordandTF wtf = it3.next();
				sum += Float.parseFloat(wtf.tf) * word_idf.get(wtf.word);				
			}
			
			
			
			
			out += "<tfidf>"+sum+"</tfidf>";
			out += "<pagerank>"+urldata.get(url).pagerank+"</pagerank>";
			
		
			out += "<title>" + urldata.get(url).title+ "</title>";
			out += "<description>" + urldata.get(url).description +"</description>";
			
			
			out += "</result>";
		}
		
		out += "</searchresults>";
		return out;
		
	}
	
